documentsByUserId.map:
function (doc, meta) {
  if("route" == doc.type){
    emit([doc.user_id, doc.type],  
         {
           "created": doc.created, 
           "name": doc.name, 
           "length": doc.length, 
           "maps": doc.maps, 
           "begin_timestamp": doc.begin_timestamp, 
           "end_timestamp": doc.end_timestamp, 
           "user_id": doc.user_id 
         }
    );
  }
  else if("friendship" == doc.type){
  	emit([doc.user_id, doc.type], doc.friend_id);
  }
  else{
 	emit([doc.user_id, doc.type], doc);
  }
}
