creds.map:
/*
	selects documents of type "user" by the key "id"
	all the listed once by the key "user_id"
	###
	routes do not return (with user 660...): 
	i guess jetty *boils the heap... (there is not even an error message...
	might also be "node.js" "elastic" (view)
	### 
*/
function (doc, meta) {
  if("user" == doc.type){
  	emit(meta.id, doc);  
  }
  else if("dog" == doc.type
          || "notification" == doc.type
          || "friendship" == doc.type
          || "gassicall" == doc.type
          || "poi" == doc.type
          || "meetingpoint" == doc.type
          || "photo" == doc.type
          || "route" == doc.type
         )
  {
  	emit(doc.user_id, doc);
  }
}

