routes.map:
function (doc, meta) {
  if("route" == doc.type){
    emit(doc.user_id, 
         {
           "created": doc.created, 
           "name": doc.name, 
           "length": doc.length, 
           "maps": doc.maps, 
           "begin_timestamp": doc.begin_timestamp, 
           "end_timestamp": doc.end_timestamp, 
           "user_id": doc.user_id 
         }
    	);
  };
}
